import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from 'src/app/services/user.service';
import { User } from 'src/app/shared/models/User';

import { IUserRegister } from 'src/app/shared/interfaces/UserRegister';
import { PasswordsMatchValidator, passwordMatchValidator } from 'src/app/shared/validators/Validators';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit, OnDestroy {

  // user: User = new User;
  regForm!:FormGroup
  isSubmitted=false
  returnUrl=''

  constructor(private userService:UserService, private fb:FormBuilder,
     private activatedRoute:ActivatedRoute,private router: Router){


  }
  ngOnDestroy(): void {
   this.regForm.reset();
  }
  ngOnInit(): void {
    this.regForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(5)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(5)]],
      confirmPassword: ['', Validators.required],
      address: ['', [Validators.required, Validators.minLength(10)]]
    },{
      // validators: PasswordsMatchValidator('password','confirmPassword')
      validators:passwordMatchValidator
    });

    this.returnUrl= this.activatedRoute.snapshot.queryParams['returnUrl'];

  }


resetForm(){
  this.regForm.reset()
}


  submitForm(){

this.isSubmitted=true;

if(this.regForm.invalid)
return;

const fv=this.regForm.value;
const user:IUserRegister={

  name: fv.name,
  email:fv.email,
  password:fv.password,
  confirmPassword:fv.confirmPassword,
  address:fv.address

};


this.userService.signupUser(user).subscribe(_=>{
  this.router.navigateByUrl(this.returnUrl)
})



  }



  // resetForm() {
  //   this.user = {
  //     id:'',
  //     name: '',
  //     password: '',
  //     email: '',
  //     address: '',
  //     isAdmin:false,
  //     token:''
  //   };
  // }
}


                <form [formGroup]="regForm" (ngSubmit)="submitForm()">


                  <div class="mb-3">

                    <input  placeholder="username" name="name" formControlName="name"  type="text" class="form-control" 

                    required   [ngClass]="{'invalid': regForm.get('name')?.invalid && (regForm.get('name')?.dirty || regForm.get('name')?.touched||isSubmitted)}">

                    <span *ngIf="regForm.get('name')?.hasError('required') &&(regForm.get('name')?.dirty || regForm.get('name')?.touched||isSubmitted)" class="error">
                      cannot be empty
                    </span>
                    <span *ngIf="regForm.get('name')?.hasError('minlength') &&(regForm.get('name')?.dirty || regForm.get('name')?.touched||isSubmitted)" class="error">
                      length must be greater or equal to 5 characters
                    </span>

                  </div>



                    <div class="mb-3">

                      <input  placeholder="email address" name="email" formControlName="email" type="email" class="form-control" 
                      [ngClass]="{'invalid': regForm.get('email')?.invalid && (regForm.get('email')?.dirty || regForm.get('email')?.touched||isSubmitted)}"     >
                        <span *ngIf="regForm.get('email')?.hasError('required') &&(regForm.get('email')?.dirty || regForm.get('email')?.touched||isSubmitted)" class="error">
                          cannot be empty
                        </span>
                        <span *ngIf="regForm.get('email')?.hasError('email') &&(regForm.get('email')?.dirty || regForm.get('email')?.touched||isSubmitted)" class="error">
                          invalid email
                        </span>

                    </div>
                    <div class="mb-3">

                      <input  placeholder="password"  name="password" formControlName="password"  type="password" class="form-control"
                      [ngClass]="{'invalid': regForm.get('name')?.invalid && (regForm.get('password')?.dirty || regForm.get('password')?.touched||isSubmitted)}" >
                      <span *ngIf="regForm.get('password')?.hasError('required') &&(regForm.get('password')?.dirty || regForm.get('password')?.touched||isSubmitted)" class="error">
                        cannot be empty
                      </span>
                      <span *ngIf="regForm.get('password')?.hasError('minlength') &&(regForm.get('password')?.dirty || regForm.get('password')?.touched||isSubmitted)" class="error">
                       length must be greater or equal to 5 characters
                      </span>
                    </div>


                    <div class="mb-3">

                      <input  placeholder="confirm-password"  name="password" formControlName="confirmPassword"  type="password" class="form-control" 
                      [ngClass]="{'invalid': regForm.get('confirmPassword')?.invalid && (regForm.get('confirmPassword')?.dirty || regForm.get('confirmPassword')?.touched||isSubmitted)}">
                      <span *ngIf="regForm.get('confirmPassword')?.hasError('required') &&(regForm.get('confirmPassword')?.dirty || regForm.get('confirmPassword')?.touched||isSubmitted)" class="error">
                        cannot be empty
                      </span>
                      <span *ngIf="regForm.get('confirmPassword')?.hasError('passwordMismatch') &&(regForm.get('confirmPassword')?.dirty || regForm.get('confirmPassword')?.touched||isSubmitted)" class="error">
                        Password and Confirm Password do not match.
                      </span>
                    </div>

                <div class="mb-3">

      <textarea  placeholder="provide your address here.."  name="address" formControlName="address"   class="form-control"   [ngClass]="{'invalid': regForm.get('address')?.invalid && (regForm.get('address')?.dirty || regForm.get('address')?.touched||isSubmitted)}" ></textarea>

                  <span *ngIf="regForm.get('address')?.hasError('required') &&(regForm.get('address')?.dirty || regForm.get('address')?.touched||isSubmitted)" class="error">
                    cannot be empty
                  </span>  
                  <span *ngIf="regForm.get('address')?.hasError('minlength') &&(regForm.get('address')?.dirty || regForm.get('address')?.touched||isSubmitted)" class="error">
                    length must be greater or equal to 10 characters
                  </span> 
                </div>


                  <div class="text-center py-2">
                    <button type="submit" class="btn btn-lg  btn-success" data-bs-dismiss="modal"  [disabled]="regForm.status==='INVALID'" >Signup</button>

                  </div> 
                </form>
