import { Component, OnInit } from '@angular/core';
import { FoodService } from 'src/app/services/food.service';
import { Food } from 'src/app/shared/models/Food';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

   foods:Food[] | undefined

constructor(private foodService:FoodService){}

  ngOnInit(): void {

this.foods=this.foodService.getAll()


  }

}
<div class="container py-5">

    <div class="row">


        <div class="col-md-3 d-flex py-3" *ngFor="let food of foods">
            <div class="card dish-card" >
              <a [routerLink]="['/food',food.id]" class="text-decoration-none" style="color: black;">
                <img src="{{food.imageUrl}}" class="card-img-top" alt="...">

                <div class="card-body">
                  <h5 class="card-title me-auto ">{{food.name}}</h5>
                  <span class="me-auto">  <star-rating [starType]="'svg'" [rating]="food.stars" [readOnly]="true"></star-rating></span>
                  <<ng-template *ngFor="let origin of food.origins">
                         {{origin}}
                </ng-template>>
                  <span class="text-secondary">{{food.price | currency:'INR'}}</span> <span style="float: right;"> {{food.cookTime}} min </span>



                <!-- </a> -->
              </div>
              </a>
                </div>


        </div>
    </div>
</div>
