import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CartService } from 'src/app/services/cart.service';
import { Cart } from 'src/app/shared/models/Cart';
import { CartItem } from 'src/app/shared/models/CartItem';

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.css']
})
export class CartPageComponent  implements OnInit{

  cart!:Cart
  constructor(private cartService:CartService){
    this.cartService.getCartObservable().subscribe((cart)=>
    this.cart=cart)


  }
  ngOnInit(): void {

  }

  removeFromCart(cartItem:CartItem){

    this.cartService.removeFromCart(cartItem.food.id)
  }

  changeQuantity(cartItem:CartItem,quantityInString:string){
    let quantity=parseInt(quantityInString);

    this.cartService.changeQuantity(cartItem.food.id,quantity)
  }

}
<p>cart-page works!</p>


<div class="container">
<div class="row">
    <div class="col-md-12">

    <div *ngFor="let cartItem of cart.items" class="content">

        <!-- <div class="block"> -->
   <span> <img [src]="cartItem.food.imageUrl" class="rounded" height="80" width="80"  alt="">
   </span>
    <!-- </div> -->

     <!-- <div  class="block"> -->

       <span> <h4>{{cartItem.food.name}}</h4>
       </span>
        <!-- </div> -->

     <!-- <div  class="block"> -->

     <span> <select #select [value]="cartItem.quantity" (change)="changeQuantity(cartItem,select.value)">
<option >1</option>
<option >2</option>
<option >3</option>
<option >4</option>
      </select>
    </span>
     <!-- </div> -->


     <!-- <div  class="block"> -->

      <span>{{cartItem.price| currency:'INR'}}</span>       
     <!-- </div> -->
    </div>
    </div>


</div>

</div>
