import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  display:string='none'

returnUrl:string=''
loginForm!:FormGroup 

constructor(private userService:UserService, private fb:FormBuilder, private router:Router,private activateRoute:ActivatedRoute){



}

  ngOnInit(): void {
   this.loginForm= this.fb.group({

      email:['',[Validators.required,Validators.email]],
      password:['',Validators.required]
    })

    this.returnUrl=this.activateRoute.snapshot.queryParams['returnUrl']

  }


  get fc(){
    return this.loginForm.controls
  }

submitForm(){

  if(this.loginForm.invalid)
   return;

   this.userService.loginUser({email:this.fc['email'].value,password:this.fc['password'].value}).subscribe(()=>{

    this.router.navigateByUrl(this.returnUrl)
   })



}

openSignup(){

this.display='block';

}
}
