import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { LatLng, LatLngTuple, Map, map, tileLayer } from 'leaflet';

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent  implements OnInit{

@ViewChild('map',{static:true}) mapRef!:ElementRef
private DEFAULTLATLNG:LatLngTuple=[30.0869, 78.2676]

map!:Map

constructor(){


}

  ngOnInit(): void {
    this.showMap()
  }


showMap(){
if(this.map)
return;

this.map=map(this.mapRef.nativeElement,{
attributionControl:false

}).setView(this.DEFAULTLATLNG,12)

tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(this.map)


}


search(){}

}
<div class="container" style="width: 30rem;height: 20rem; position: relative;">
    <button class="btn search" (click)="search()" > Search My Location</button>
<div  #map class="map" style="width: 100%;height: 100%;">

</div>


</div>
