import { Component, OnInit } from '@angular/core';
import { FoodService } from 'src/app/services/food.service';
import { Food } from 'src/app/shared/models/Food';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

   foods:Food[] | undefined

constructor(private foodService:FoodService){}

  ngOnInit(): void {

this.foods=this.foodService.getAll()


  }

}
<div class="container py-5">

    <div class="row">


        <div class="col-md-3 py-3" *ngFor="let food of foods">
            <div class="card" >
              <!-- <a [routerLink]="['/food',food.id]"> -->
                <img src="{{food.imageUrl}}" class="card-img-top" alt="...">

                <div class="card-body">
                  <h5 class="card-title">{{food.name}}</h5>

                  <star-rating [starType]="'svg'" [rating]="food.stars" [readOnly]="true"></star-rating>

                  <ul class="list-group list-group-flush">
                    <li class="list-group-item"></li>
                    <li class="list-group-item">{{food.cookTime}}</li>
                    <li class="list-group-item">{{food.price | currency:'INR'}}</li>
                  </ul>
                <!-- </a> -->
              </div>
                  <!-- <a href="#" class="btn btn-primary">Go somewhere</a> -->
                </div>


        </div>
    </div>
</div>
