import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CartService } from 'src/app/services/cart.service';
import { Cart } from 'src/app/shared/models/Cart';
import { CartItem } from 'src/app/shared/models/CartItem';

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.css']
})
export class CartPageComponent  implements OnInit{

  cart!:Cart
  constructor(private cartService:CartService){
    this.cartService.getCartObservable().subscribe((cart)=>
    this.cart=cart)


  }
  ngOnInit(): void {

  }

  removeFromCart(cartItem:CartItem){

    this.cartService.removeFromCart(cartItem.food.id)
  }

  changeQuantity(cartItem:CartItem,quantityInString:string){
    let quantity=parseInt(quantityInString);

    this.cartService.changeQuantity(cartItem.food.id,quantity)
  }

}

<!-- app.component.html -->

<div class="container my-4">
    <div class="row">
      <div class="col-md-8">
        <h2 class="mb-3">Your Cart</h2>
        <div class="cart-items">
          <div class="cart-item" *ngFor="let cartItem of cart.items">
            <img src="{{ cartItem.food.imageUrl }}" alt="Food Item" class="img-fluid cart-item-image">
            <div class="cart-item-details">
              <div class="cart-item-title">{{ cartItem.food.name }}</div>
              <div class="cart-item-price">Price: ${{ cartItem.food.price.toFixed(2) }}</div>
              <div class="cart-item-quantity">Quantity: {{ cartItem.quantity }}</div>
            </div>
          </div>
        </div>
      </div>
      <div class="col-md-4">
        <div class="checkout-summary">
          <!-- Display checkout summary, total price, and payment options here -->
        </div>
      </div>
    </div>
  </div>
